import React from "react";

function Navbar() {
  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <nav className="fixed top-0 left-0 w-full bg-white border-b border-gray-200 shadow-sm z-50">
      <div className="max-w-[1400px] mx-auto flex items-center justify-between px-6 py-4">
        {/* Logo */}
        <div className="font-poppins font-bold text-[22px] text-[#7755FF] cursor-pointer">
          Dimas<span className="text-[#FF54B0]">.</span>
        </div>

        {/* Menu (Desktop) */}
        <ul className="hidden md:flex items-center gap-6 text-[16px] font-poppins font-semibold text-[#000000]">
          <li
            className="cursor-pointer hover:text-[#7755FF] transition-colors"
            onClick={() => scrollToSection("about")}
          >
            About
          </li>
          <li
            className="cursor-pointer hover:text-[#7755FF] transition-colors"
            onClick={() => scrollToSection("skills")}
          >
            Skills
          </li>
          <li
            className="cursor-pointer hover:text-[#7755FF] transition-colors"
            onClick={() => scrollToSection("portfolio")}
          >
            Portfolio
          </li>
          <li
            className="cursor-pointer hover:text-[#7755FF] transition-colors"
            onClick={() => scrollToSection("contact")}
          >
            Contact
          </li>
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
