import React from "react";
import { FaInstagram, FaGithub, FaLinkedin } from "react-icons/fa";
import profileImage from "../assets/images/IMG-20231206-WA0022.jpg";

function ProfileCard() {
  return (
    <section className="w-full bg-gradient-to-r from-[#E0EAFE] via-[#F9E5FF] to-[#8EC5FC] py-28 md:py-36">
      <div className="w-full flex flex-col-reverse md:flex-row items-center justify-between px-16 gap-16">
        {/* ===== LEFT TEXT ===== */}
        <div className="text-left md:w-1/2">
          <p className="text-[#7755FF] font-semibold mb-3 text-[15px]">Hello, Iâ€™m</p>
          <h1 className="text-[42px] md:text-[52px] font-extrabold text-[#1F1F39] leading-tight mb-5">
            Creative Designer & <br /> Frontend Developer
          </h1>
          <p className="text-[#555] text-[17px] leading-relaxed mb-10 max-w-md">
            Passionate in creating beautiful and functional digital experiences.
            I love blending creativity with technology to make impactful
            solutions for users and brands.
          </p>

          {/* Buttons */}
          <div className="flex gap-4 mb-10">
            <button className="bg-gradient-to-r from-[#7755FF] to-[#FF54B0] text-white font-semibold px-6 py-3 rounded-lg shadow-md hover:opacity-90 transition-all duration-200">
              See My Work
            </button>
            <button className="border-2 border-[#7755FF] text-[#7755FF] font-semibold px-6 py-3 rounded-lg hover:bg-[#7755FF] hover:text-white transition-all duration-200">
              Contact Me
            </button>
          </div>

          {/* Social Icons */}
          <div className="flex items-center gap-5 text-[22px] text-[#1F1F39]">
            <FaInstagram className="cursor-pointer hover:text-[#7755FF] transition-colors" />
            <FaGithub className="cursor-pointer hover:text-[#7755FF] transition-colors" />
            <FaLinkedin className="cursor-pointer hover:text-[#7755FF] transition-colors" />
          </div>
        </div>

        {/* ===== RIGHT IMAGE ===== */}
        <div className="md:w-1/2 flex justify-center relative md: justify-end relative pr-6"> 
          {/* Background Gradient Circle */}
          <div className="absolute -top-8 -right-10 w-[260px] h-[260px] bg-gradient-to-br from-[#7755FF]/30 to-[#FF54B0]/40 rounded-full blur-3xl"></div>

          {/* Main Image */}
          <img
            src={profileImage}
            alt="Profile"
            className="relative w-[256px] h-[256px] rounded-full border-[8px] border-white shadow-2xl object-cover"
          />
        </div>
      </div>
    </section>
  );
}

export default ProfileCard;
