import React, { useState } from "react";
import { FaPaperPlane } from "react-icons/fa";

function Navbar() {
   const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: "smooth" });
    }
  };
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <nav className="fixed top-0 left-0 w-full bg-white border-b border-gray-200 shadow-sm z-50">
      <div className="w-full flex justify-between items-center px-16 py-5">
        {/* Logo */}
        <div className="font-poppins font-bold text-[20px] text-black tracking-tight select-none">
          My<span className="#00000000">Profile</span>
        </div>

        {/* Menu (Desktop) */}
        <ul className="hidden md:flex items-center gap-6 text-[16px] font-poppins font-semibold text-[#000000]">
          <li className="cursor-pointer hover:text-[#7755FF] transition-colors" onClick={() => scrollToSection("about")}>About</li>
          <li className="cursor-pointer hover:text-[#7755FF] transition-colors" onClick={() => scrollToSection("skills")}>Skills</li>
          <li className="cursor-pointer hover:text-[#7755FF] transition-colors" onClick={() => scrollToSection("portfolio")}>Portfolio</li>
          <li className="cursor-pointer hover:text-[#7755FF] transition-colors">Contact</li>
        </ul>

        {/* Tombol Hire Me */}
        <button className="hidden md:flex items-center gap-2 bg-gradient-to-r from-[#7755FF] to-[#FF54B0] text-white font-poppins font-semibold text-[16px] px-4 py-2 rounded-lg shadow hover:opacity-90 transition-all duration-200">
          <FaPaperPlane className="text-[16px]" />
          Hire Me
        </button>

        {/* Tombol Menu Mobile */}
        <button
          className="md:hidden text-2xl text-gray-700"
          onClick={() => setMenuOpen(!menuOpen)}
        >
          {menuOpen ? "✕" : "☰"}
        </button>
      </div>

      {/* Menu Mobile */}
      {menuOpen && (
        <div className="md:hidden bg-white border-t border-gray-200 shadow-md px-6 py-4 space-y-3 text-[#1F1F39] font-medium">
          <a href="#" className="block hover:text-[#7755FF] transition">About</a>
          <a href="#" className="block hover:text-[#7755FF] transition">Skills</a>
          <a href="#" className="block hover:text-[#7755FF] transition">Portfolio</a>
          <a href="#" className="block hover:text-[#7755FF] transition">Contact</a>

          {/* Hire Me Mobile */}
          <button className="w-full flex items-center justify-center gap-2 bg-gradient-to-r from-[#7755FF] to-[#FF54B0] text-white font-semibold text-[14px] px-5 py-2 rounded-lg shadow hover:opacity-90 transition">
            <FaPaperPlane className="text-[13px]" />
            Hire Me
          </button>
        </div>
      )}
    </nav>
  );
}

export default Navbar;
