import React from "react";

function AboutSection() {
  return (
    <section className="py-16 bg-white">
      <div className="max-w-5xl mx-auto px-6 text-center">
        <h2 className="text-2xl font-bold text-gray-800 mb-4">Tentang Saya</h2>
        <p className="text-gray-600 leading-relaxed mb-6">
          Saya adalah seorang UI/UX Designer & Web Developer berdomisili di Jakarta
          dengan pengalaman lebih dari 5 tahun di industri kreatif. 
          Passion saya adalah menciptakan solusi digital yang indah secara visual, 
          tapi juga mudah digunakan dan berdampak positif bagi pengguna.
        </p>
        <div className="flex flex-wrap justify-center gap-3 mb-6">
          {["Problem Solver", "Team Player", "Fast Learner", "Kreatif & Adaptif"].map((tag) => (
            <span key={tag} className="bg-gray-100 text-gray-700 px-3 py-1 rounded-full text-sm">
              {tag}
            </span>
          ))}
        </div>
        <button className="bg-gradient-to-r from-blue-500 to-purple-500 text-white px-6 py-2 rounded-lg">
          Konsultasi Gratis
        </button>
      </div>
    </section>
  );
}

export default AboutSection;
